figure.artwork:
enum { x509_entry(0), precert_entry(1), (65535) } LogEntryType;

struct {
    LogEntryType entry_type;
    select (entry_type) {
        case x509_entry: X509ChainEntry;
        case precert_entry: PrecertChainEntry;
    } entry;
} LogEntry;

opaque ASN.1Cert<1..2^24-1>;

struct {
    ASN.1Cert leaf_certificate;
    ASN.1Cert certificate_chain<0..2^24-1>;
} X509ChainEntry;

struct {
    ASN.1Cert tbs_certificate;
    ASN.1Cert precertificate_chain<1..2^24-1>;
} PrecertChainEntry;

enum { certificate_timestamp(0), tree_hash(1), 255 }
  SignatureType;

enum { v1(0) }
  Version;

  struct {
      opaque key_id[32];
  } LogID;

struct {
    Version version;
    LogID id;
    uint64 timestamp;
    digitally-signed struct {
        SignatureType signature_type = certificate_timestamp;	    
        uint64 timestamp;
        LogEntryType entry_type;
        select(entry_type) {
            case x509_entry: ASN.1Cert;
            case precert_entry: ASN.1Cert;
        } signed_entry;
    };
} SignedCertificateTimestamp;

struct {
    uint64 timestamp;
    LogEntryType entry_type;
    select(entry_type) {
        case x509_entry: ASN.1Cert;
        case precert_entry: ASN.1Cert;
    } signed_entry;
} MerkleTreeLeaf;

struct {
    Version version;
    LogID id;
    uint64 old_tree_size;
    uint64 timestamp;
    MerkleTreeLeaf new_leaves<0..2^64-1>;
    digitally-signed struct {
        SignatureType signature_type = tree_hash;
        uint64 timestamp;
        uint64 tree_size;
        opaque sha256_root_hash[32];
    } TreeHeadSignature;
} SignedMerkleTreeUpdate;

struct {
    Version version;
    LogID id;
    uint64 tree_size;
    uint64 timestamp;
    uint64 leaf_index;
    MerkleNode audit_path<0..2^16-1>;
    TreeHeadSignature tree_head_signature;
} MerkleAuditProof;
